using System;

namespace Kaiyuanshe.OpenHackathon.Server.Models
{
    /// <summary>
    /// Describes a running system on cloud.
    /// </summary>
    [Obsolete]
    public class Experiment : KubernetesModelBase
    {
        /// <summary>
        /// name of the hackathon.
        /// </summary>
        /// <example>foo</example>
        public string hackathonName { get; internal set; }

        /// <summary>
        /// auto-generated id of the experiment.
        /// </summary>
        /// <example>6129c741-87e5-4a78-8173-f80724a70aea</example>
        public string id { get; internal set; }

        /// <summary>
        /// id(Guid) of template. It is auto-generated by hackathon server. 
        /// </summary>
        /// <example>31009bb6-be04-4ba1-901b-21e2b5b0f714</example>
        public string templateId { get; set; }

        /// <summary>
        /// id of user
        /// </summary>
        /// <example>1</example>
        public string userId { get; internal set; }

        /// <summary>
        /// Detailed user info of the member
        /// </summary>
        public UserInfo user { get; internal set; }

        /// <summary>
        /// Uri to access the experiment via remote connection. 
        /// Be sure to append the access token of current login user to the uri. 
        /// Response from the api call DOESN'T include token value. 
        /// </summary>
        /// <example>https://guacamole.kaiyuanshe.cn/guacamole/#/?hackathon=foo&amp;experiment=6129c741-87e5-4a78-8173-f80724a70aea&amp;token=</example>
        public string remoteConnectionUri { get; internal set; }
    }

    public class ExperimentList : ResourceList<Experiment>
    {
        /// <summary>
        /// a list of experiments.
        /// </summary>
        public override Experiment[] value { get; set; }
    }
}
